package gov.noaa.pmel.tmap.las.ui;

import gov.noaa.pmel.tmap.exception.LASException;
import gov.noaa.pmel.tmap.las.jdom.LASConfig;
import gov.noaa.pmel.tmap.las.product.server.LASConfigPlugIn;
import gov.noaa.pmel.tmap.las.util.Dataset;

import java.io.IOException;
import java.io.PrintWriter;
import java.io.UnsupportedEncodingException;
import java.net.URLDecoder;
import java.util.ArrayList;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.jdom.JDOMException;
import org.json.JSONException;
import org.json.JSONObject;
import org.json.XML;

/** 
 * MyEclipse Struts
 * Creation date: 01-05-2007
 * 
 * XDoclet definition:
 * @struts.action validate="true"
 */
public class GetDatasets extends ConfigService {
    /*
     * Generated Methods
     */

    /** 
     * Method execute
     * @param mapping
     * @param form
     * @param request
     * @param response
     * @return ActionForward
     */
    private static Logger log = LoggerFactory.getLogger(GetDatasets.class.getName());
    public String execute() {
		String query = request.getQueryString();
		if ( query != null ) {
			try{
				query = URLDecoder.decode(query, "UTF-8");
				log.info("START: "+request.getRequestURL()+"?"+query);
			} catch (UnsupportedEncodingException e) {
				// Don't care we missed a log message.
			}			
		} else {
			log.info("START: "+request.getRequestURL());
		}
		String data_url = request.getParameter("data_url");
		
        String format = request.getParameter("format");
        if ( format == null ) {
            format = "json";
        }
        String startString = request.getParameter("start");
        String endString = request.getParameter("end");
        
        int start = 0;
        int end = 0;
        
        // Both zero means all.
        if ( startString != null ) {
            try {
                start = Integer.valueOf(startString);
            } catch (NumberFormatException e) {
               start = 0;
            }
        }
        if ( endString != null ) {
            try {
                end = Integer.valueOf(endString);
            } catch (NumberFormatException e) {
                end = 0;
            }
        } else {
            end = 0;
        }
        // Get the LASConfig (sub-class of JDOM Document) from the servlet context.
        log.debug("Processing request for dataset list.");
        
        String lock = (String) contextAttributes.get(LASConfigPlugIn.LAS_LOCK_KEY);
        if ( lock != null && lock.equals("true") ) {
        	sendError(response, "datasets", format, "Site updating. Reload and try again in a minute.");
        	return null;
        }
        
        LASConfig lasConfig = (LASConfig) contextAttributes.get(LASConfigPlugIn.LAS_CONFIG_KEY); 
                
        ArrayList<Dataset> datasets = new ArrayList<Dataset>();
		try {
			datasets = lasConfig.getDatasets(true, start, end, data_url);
			
			
			
		} catch (JDOMException e) {
			sendError(response, "<datasets>", format, e.getMessage());
		} catch (LASException e) {
			sendError(response, "<datasets>", format, e.getMessage());
		}
        StringBuffer xml = new StringBuffer();
        
        xml.append("<datasets>");
        for (int i = 0; i < datasets.size(); i++) {
            Dataset d = datasets.get(i);
            xml.append(d.toXML());
        }
        xml.append("</datasets>");
        
        try {
            PrintWriter respout = response.getWriter();
            if (format.equals("xml")) {
                response.setContentType("application/xml");
                respout.print(xml.toString());
            } else {
                response.setContentType("application/json");
                JSONObject json_response = XML.toJSONObject(xml.toString());
                log.debug(json_response.toString(3));
                json_response.write(respout);
            }
        } catch (IOException e) {
        	sendError(response, "<datasets>", format, e.getMessage());
        } catch (JSONException e) {
        	sendError(response, "<datasets>", format, e.getMessage());
        }   
        if ( query != null ) {
			log.info("END:   "+request.getRequestURL()+"?"+query);						
		} else {
			log.info("END:   "+request.getRequestURL());
		}
        return null;
    }
}